    erro = value.length > 50 ? 'Escreva algo' : '';

    return (
      <label>
        Vai Comparecer  
        <br />
        <input
          type="checkbox"
          name="vaiComparecer"
          value={value}
          onChange={handleChange}
        />
      </label>
    )
  }
}

export default VaiComparecer
